<form method="post" id="authForm">
    <div class="error-message-modal alert alert-danger"></div>
    <div class="success-message-modal alert alert-success"></div>
    Логин
    <input type="text" class="form-control form-control-sm"
           name="auth_email" id="auth_email"/>
    Пароль
    <input type="password" class="form-control form-control-sm"
           name="auth_pass" id="auth_pass"/>
    <br/>
    <input id="auth_submit" type="submit"
           class="btn  btn-primary" value="Вход"/>
</form>
